import React, { useState } from 'react';

function Sidebar({ initialMenuItems }) {
  // Step 2: Initialize states
  const [menuItems, setMenuItems] = useState(initialMenuItems || []);
  const [newMenuItem, setNewMenuItem] = useState('');
  const [filter, setFilter] = useState('');

  // Step 3: Function to add a new menu item
  const addMenuItem = () => {
    if (newMenuItem.trim() !== '') {
      setMenuItems([...menuItems, newMenuItem]);
      setNewMenuItem(''); // Clear the input
    }
  };

  // Step 4: Filter the menu items based on the filter
  const filteredItems = menuItems.filter(item =>
    item.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div>
      {/* Input to add a new menu item */}
      <input
        type="text"
        id="newMenuItemValue"
        value={newMenuItem}
        onChange={(event) => setNewMenuItem(event.target.value)}
      />
      <br />

      {/* Rendered unordered list of filtered menu items */}
      <ul>
        {filteredItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>

      {/* Button to add a new item to the list */}
      <button onClick={addMenuItem}>Add Item</button>
      <br />

      {/* Input field to filter menu items */}
      <input
        id="filter"
        type="text"
        value={filter}
        onChange={(event) => setFilter(event.target.value)}
        placeholder="Filter by..."
      />
    </div>
  );
}

export default Sidebar;
